changeSet.sql:
CALL apoc.periodic.iterate(
  "MATCH (g:Gene) RETURN g",
  "
  CALL apoc.path.spanningTree(g, {
      relationshipFilter: "IS",
      labelFilter: "+Gene"
  })
  YIELD path
  UNWIND nodes(path) as gene
  WITH gene.eid as gene_eid ORDER BY gene_eid
  SET g.meta_id = apoc.text.join(collect(DISTINCT gene_eid), '|')
  ",
  {batchSize:5000, params: {rows: $rows}}
);
